
function EditNotification(){$("#StartDate").datetimepicker({format:"DD/MM/YYYY"});$("#EndDate").datetimepicker({format:"DD/MM/YYYY"});$("#StartDate").on("dp.change",function(a){$("#EndDate").data("DateTimePicker").minDate(a.date)});$("#EndDate").on("dp.change",function(a){$("#StartDate").data("DateTimePicker").maxDate(a.date)});$("#LinkType").change(function(){if($(this).val()==="INTERNAL"){$("#InternalLinkGroup").slideDown();$("#ExternalLinkGroup").slideUp()}else{$("#InternalLinkGroup").slideUp();$("#ExternalLinkGroup").slideDown()}})}EditNotification.prototype.loadInternalLinks=function(b){var a=new $.JsonRpcClient({ajaxUrl:"WebServices/GetApplicationLinks.php"});a.call("get",{applicationId:b},function(e){if(e.Result===true){var c=$("#InternalLinks");c.empty();for(var d=0;d<e.ApplicationLinks.length;d++){c.append('<option value="'+e.ApplicationLinks[d].id+'">'+e.ApplicationLinks[d].name+"</option>")}}},function(c){})};$(function(){var a=new EditNotification();$("#ApplicationId").change(function(){a.loadInternalLinks($("#ApplicationId").val())})});